package javaloopStatements;

public class oddNumbers1to100 {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Below are the odd numbers between 1-100:");

		// print even no
		for(int i=1; i<=100; i++) {
			if(i%2!=0) {

				System.out.println(i);
			}
		}

}
}